<?phpfunction the_deals_shortcode() {    ob_start();    ?>    <style type='text/css'>        .logo-block {            text-align: center;            background: #F6F6F6;            padding: 18px;        }        .logo-block a{            display: inline-block;            cursor: pointer;        }        .deals-row{            margin-top: 25px;        }        .modal-body{            text-align: center;        }        body.layout-full{ padding-right: 0 !important; }        .click-here{            background: #F48631;            cursor: pointer;            color: #fff;            font-size: 16px;            line-height: 28px;            border-radius: 3px;            margin-top: 5px;        }        .click-here:hover{            background: #F16900;        }    </style>     <div class="row deals-row"> <!-- 1st Row Start-->                        <?php         query_posts(array('post_type' => 'the_deals', 'order' => 'ASC', 'posts_per_page' => -1));        if (have_posts()) : while (have_posts()) : the_post();                        $slug = basename( get_permalink( get_the_ID() ) );                $title = explode('|', get_the_title());                $title1 = $title['0'];        $title2 = $title['1'];                if( $title2 == ''){            $class = 'single-row';        }else{            $class = '';        }                        ?>                  <div class="vc_col-sm-4 <?php echo $class; ?>">	                                    <!-- DAPHNE Block Start-->	            <div class="vc_col-sm-4 ">	                <div class="icon-block">                    <div class="icon-box">					                            <img class="alignleft size-full" src="<?php bloginfo('template_directory'); ?>/images/theDeals/deal-icon.jpg" alt="deal-icon" width="80" height="79">                    </div>                    <div class="text-box">                        <p><?php echo $title1; ?></p>                        <p><?php echo $title2; ?></p>                    </div>                </div>	                                <div class="logo-block">                    <a data-toggle="modal" data-target="#<?php echo $slug; ?>">                        <?php                            if (has_post_thumbnail()) :                                the_post_thumbnail('full');                            endif;                        ?>                                            </a>	                    <div data-toggle="modal" data-target="#<?php echo $slug; ?>" class='click-here'>Click Here</div>                </div> 	                                <!-- Modal -->                <div class="modal fade" id="<?php echo $slug; ?>" tabindex="-1" role="dialog" aria-labelledby="myModalLabel">                  <div class="modal-dialog" role="document">                        <div class="modal-content">                          <div class="modal-header">                                <button type="button" class="close" data-dismiss="modal" aria-label="Close"><span aria-hidden="true">&times;</span></button>                          </div>                          <div class="modal-body">                                <?php the_content(); ?>                          </div>				                          </div>                  </div>                </div>			             </div><!-- Daphne FRAGRANCE Block END-->                                           </div>                <?php endwhile; else: ?>            <p><?php _e('Sorry, no posts matched your criteria.'); ?></p>        <?php endif; ?>        <?php wp_reset_query(); ?>     </div>    <?php    $html = ob_get_contents();    ob_end_clean();    return $html;}add_shortcode('the_deals', 'the_deals_shortcode');